import { Outlet, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { v4 as uuid } from 'uuid';
import Sidebar from './Sidebar';

function Layout() {

    ///
    const navigate = useNavigate();

    const handleButtonClick = (component) => {
      navigate(component);
    }
    ///

    const hideSidebar = () => {
      const sidebar = document.querySelector('.sidebar');
      const content = document.querySelector('.main');
      sidebar.classList.toggle('hidden');
      content.classList.toggle('new-main');
    }

    ///NEW STUFF
    const [notes, setNotes] = useState(localStorage.notes ? JSON.parse(localStorage.notes) : []);


    const [activeNote, setActiveNote] = useState(false);
    const [isNoteAdded, setNoteAdded] = useState(false);

    useEffect(() => {
      localStorage.setItem('notes', JSON.stringify(notes));
    }, [notes]);

    const onUpdateNote = (updatedNote) => {
      const newNotesArray = notes.map((note) => {
        if (note.id === activeNote) {
          if (updatedNote.title !== lastTitle || updatedNote.body !== note.body) {
            updatedNote.lastModified = new Date.now();
          }
          else{
            updatedNote.lastModified = note.lastModified;
          }
          setLastTitle(updatedNote.title);
        }
        return note;
      });
      setNotes(newNotesArray);
      setNoteAdded(true);
    };

    const onDeleteNote = (idToDelete) => {
      const confirmDelete = window.confirm("Are you sure you want to delete this note?");
      if (confirmDelete) {
        setNotes((prevNotes) => prevNotes.filter((note) => note.id !== idToDelete));
      }
    };

    const getActiveNote = () => {
      return notes.find((note) => note.id === activeNote);
    };
    ///

    const onAddNote = () => {
      const newNote = {
        id: uuid(),
        title: "Untitled",
        body: "",
        date: Date.now(),
      };
      handleButtonClick('/edit');
      setNotes((prevNotes) => [newNote, ...prevNotes]);
      setActiveNote(newNote.id);
      setNoteAdded(true);
      setLastTitle(newNote.title);
      // handleButtonClick('/edit');
    };
     ///

    return (
      <>
        <div className="header">         
          <div className="menu">
            <button onClick={hideSidebar}>&#9776;</button>
          </div>

          <div className="center-header">
            <h1><b>Lotion</b></h1>
            <h5>Like Notion, but worse!</h5> 
          </div>
        </div>

        <div id="body">
          {/* Sidebar section */}
          <div className="sidebar"><Sidebar notes={notes} onAddNote={onAddNote} activeNote={activeNote} setActiveNote={setActiveNote}/></div>

          {/* Content section */}
          <div id="content" className="main">

          {/* child components get injected here and replace <Outlet /> */}
          <Outlet activeNote={getActiveNote()} onUpdateNote={onUpdateNote} onDeleteNote={onDeleteNote} isNoteAdded={isNoteAdded}/>
          </div>
        </div>
      </>
      //  notes={notes} onAddNote={onAddNote}
      // onButtonClick={handleButtonClick} 
    )
  }

export default Layout;
