import React, {useState, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Edit({onDeleteNote, onUpdateNote, activeNote, isNoteAdded}) {
    // const [, setValue] = useState('');

    const onEditField = (field, value) => {
      onUpdateNote({
        ...activeNote,
        [field]: value,
        date: Date.now(),
      });
    }

    const reference = useRef();

    const saveNote = () => {
      const updatedNote = {
        ...activeNote,
        body: reference.current.getEditor().getText(),
        date: new Date().toLocalDateString(),
      };
      onUpdateNote(updatedNote);
    };

    return (
        <>
        <div id="edit-header">
          <input className='edit-title' type="text" placeholder="Untitled" autoFocus value={activeNote.title} 
          onChange={(e) => onEditField("title", e.target.value)}/>
          <input type="datetime-local"/>
          <button onClick={() => saveNote()}>Save</button>
          <button onClick={() => onDeleteNote(activeNote.id)}>Delete</button>
        </div>
        <ReactQuill theme="snow" value={activeNote.body} ref={reference} onChange={setValue} placeholder="Your note here"/>
        </>
    );
  };

export default Edit;
