import React from 'react';

function Sidebar ({notes, onAddNote, activeNote, setActiveNote}){

    // const [notes, setNotes] = useState([]); 

    // const onAddNote = () => {

    //   const newNote = {
    //      id: uuid(),

    //      title: "Untitled",

    //      body: "",

    //      date: Date.now(),

    //    };
    //    setNotes([newNote, ...notes]);
    //  };
    // const handleButtonClick = () => {
    // props.onButtonClick('/edit');
    // }
    const sortedNotes = notes.sort((a, b) => b.date - a.date);

    return (
        <>
            <div className="sidebar-header">
              <h1><b>Notes</b></h1>

              <button onClick={onAddNote}><b>+</b></button>
            </div>

            <div className="sidebar-content">
              {sortedNotes.map(({id, title, body, date}, i) => (
              <div className={`sidebar-note${id === activeNote && "active"}`} onClick={()=> setActiveNote(id)}>

                <div className="sidebar-note-title">
                  <strong>{title}</strong>
                </div>

                <p>{body && body.substr(0, 100) + "..."}</p>

                <small className="sidebar-note-date">Last modified {new Date(date).toLocaleDateString("en-CA", 
                {hour: "2-digit",
                minute: "2-digit"
                })}</small>
              </div>
              ))}
            </div>
            {/* ,  {notes, onAddNote} */}
        </>
    )
}

export default Sidebar;
